PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `Users` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `email` VARCHAR(255) NOT NULL UNIQUE, `password_hash` VARCHAR(255), `name` VARCHAR(255) NOT NULL, `phone` VARCHAR(255), `role` TEXT DEFAULT 'client', `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Referrals` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `userId` INTEGER NOT NULL REFERENCES `Users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, `code` VARCHAR(255) NOT NULL UNIQUE, `prospectEmail` VARCHAR(255), `prospectName` VARCHAR(255), `selectedReward` VARCHAR(255) NOT NULL, `status` TEXT DEFAULT 'Open', `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Estimates` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `referralId` INTEGER NOT NULL REFERENCES `Referrals` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, `name` VARCHAR(255) NOT NULL, `email` VARCHAR(255) NOT NULL, `phone` VARCHAR(255) NOT NULL, `address` VARCHAR(255) NOT NULL, `city` VARCHAR(255), `description` TEXT, `status` VARCHAR(255) DEFAULT 'Pending', `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `RewardSettings` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` VARCHAR(255) NOT NULL UNIQUE, `active` TINYINT(1) DEFAULT 1, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
DELETE FROM sqlite_sequence;
COMMIT;
